var now=new Date,startTime=new Date("06/01/2025 00:00:00"),interval=250,workboard=document.getElementById("workboard");function createtime(){now.setTime(now.getTime()+interval);var diffInMs=now-startTime,days=Math.floor(diffInMs/864e5),hours=Math.floor(diffInMs%864e5/36e5),minutes=Math.floor(diffInMs%36e5/6e4),seconds=Math.round(diffInMs%6e4/1e3);minutes=minutes<10?"0"+minutes:minutes,seconds=seconds<10?"0"+seconds:seconds;var imgSrc=(hours=hours<10?"0"+hours:hours)<18&&hours>=9?"https://npm.elemecdn.com/anzhiyu-blog@2.0.3/img/badge/安知鱼-上班摸鱼中.svg":"https://npm.elemecdn.com/anzhiyu-blog@2.0.3/img/badge/安知鱼-下班啦.svg",imgTitle=hours<18&&hours>=9?"距离月入25k也就还差一个大佬带我~":"下班了就该开开心心的玩耍，嘿嘿~";workboard?workboard.innerHTML=`\n            <img class='boardsign' src='${imgSrc}' title='${imgTitle}'>\n            <span class='textTip'><br> 本站居然运行了 ${days} 天</span>\n            <span id='runtime'> ${hours} 小时 ${minutes} 分 ${seconds} 秒喵~ </span>\n            <i class='fas fa-heartbeat' style='color:red'></i>\n        `:console.error("元素 'workboard' 未找到！")}setInterval(createtime,interval);